import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Leaderboard.css';

const LeaderboardPage = () => {
    const [leaderboard, setLeaderboard] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch leaderboard data initially and set up periodic updates
        const fetchLeaderboard = async () => {
            try {
                const response = await axios.get('https://api.example.com/leaderboard');
                setLeaderboard(response.data.slice(0, 5)); // Display top 5 users
            } catch (error) {
                console.error("Error fetching leaderboard data:", error);
            }
        };

        fetchLeaderboard();

        // Poll leaderboard data every 10 seconds to update in real-time
        const intervalId = setInterval(fetchLeaderboard, 10000);
        return () => clearInterval(intervalId);
    }, []);

    const handleLogout = () => {

        localStorage.removeItem('authToken');
        navigate('/');
    };

    return (
        <div className="leaderboard-page">
            <h2>Leaderboard</h2>
            <table className="leaderboard-table">
                <thead>
                    <tr>
                        <th>Rank</th>
                        <th>Name</th>
                        <th>Score</th>
                    </tr>
                </thead>
                <tbody>
                    {leaderboard.map((user, index) => (
                        <tr key={user.id}>
                            <td>{index + 1}</td>
                            <td>{user.name}</td>
                            <td>{user.score}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button className="logout-button" onClick={handleLogout}>
                Logout
            </button>
        </div>
    );
};

export default LeaderboardPage;
