import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './QuizPage.css';

const QuizPage = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [userAnswers, setUserAnswers] = useState({}); // To track user's answers
    const [timeRemaining, setTimeRemaining] = useState(300); 
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('https://krish-2512.github.io/api/questions-2.json');
                const shuffled = response.data.sort(() => 0.5 - Math.random()); // Shuffle questions randomly
                const selectedQuestions = shuffled.slice(0, 10); // Take only 10 questions
                setQuestions(selectedQuestions);
                setCorrectAnswers(selectedQuestions.map(q => q.answer)); // Store correct answers
            }catch (err) {
                console.error("Error loading questions:", err);
            }
        };

        fetchQuestions();
    }, []);

    useEffect(() => {
        if (timeRemaining <= 0) {
            handleFinalSubmit(); // Automatically submit if time runs out
        }
        const timerId = setInterval(() => {
            setTimeRemaining((prev) => prev - 1);
        }, 1000);
        return () => clearInterval(timerId);
    }, [timeRemaining]);

    const handleAnswerChange = (answer) => {
        setUserAnswers((prev) => ({ ...prev, [currentQuestion]: answer }));
    };

    const handleSubmitAnswer = () => {
        // Simply saves the answer without changing questions
        console.log("Answer submitted for question:", currentQuestion, userAnswers[currentQuestion]);
        setCurrentQuestion((prev) => prev + 1);
    };

    const handleNextQuestion = () => {
        // Go to next question
        setCurrentQuestion((prev) => prev + 1);
    };

    const handleFinalSubmit = () => {
        // Handle the final submission logic
        console.log("Final submission of all answers:", userAnswers);
        // navigate('/result'); // Redirect to result page
        handleQuizCompletion();
    };
    const handleQuizCompletion = () => {
        // Calculate the score based on user answers and correct answers
        const score = calculateScore(userAnswers, correctAnswers);
        // Navigate to the result page and pass relevant state
        navigate('/result', { state: { score, totalQuestions: questions.length, userAnswers, correctAnswers } });
    };
    const calculateScore = (userAnswers, correctAnswers) => {
        let score = 0;
        // Logic to compare answers (pseudo-code)
        for (let questionIndex in correctAnswers) {
            if (userAnswers[questionIndex] === correctAnswers[questionIndex]) {
                score += 1; // Increment score for each correct answer
            }
        }
        return score;
    };
    
    if (questions.length === 0) return <div>Loading...</div>;
    const minutes = Math.floor(timeRemaining / 60);
    const seconds = timeRemaining % 60;
    const rotation = (timeRemaining / 300) * 360;

    return (
        <div className="quiz-page">
        <div className="quiz-container">
            <h3>Question {currentQuestion + 1} of 10</h3>
            <div className="timer-container">
                <div className="timer-title">Time Remaining</div>
                <div className="circular-timer">
                    <div className="timer-inner">
                        {minutes < 10 ? `0${minutes}` : minutes}:{seconds < 10 ? `0${seconds}` : seconds}
                    </div>
                    <div className="loader"></div>
                </div>
            </div>

            <div className="question">
                <h4>{questions[currentQuestion]?.question}</h4>
                <ul>
                    {['A', 'B', 'C', 'D'].map((option) => (
                        <li key={option}>
                            <label>
                                <input
                                    type="radio"
                                    name={`question-${currentQuestion}`}
                                    value={option}
                                    onChange={() => handleAnswerChange(option)}
                                    checked={userAnswers[currentQuestion] === option}
                                />
                                {questions[currentQuestion][option]}
                            </label>
                        </li>
                    ))}
                </ul>
            </div>

            <div className="navigation-buttons">
                {currentQuestion > 0 && (
                    <button onClick={() => setCurrentQuestion((prev) => prev - 1)}>
                        Previous Question
                    </button>
                )}

                {currentQuestion < questions.length - 1 && (
                    <button onClick={handleNextQuestion}>
                        Next Question
                    </button>
                )}
                {currentQuestion < questions.length - 1 && (
                    <button onClick={handleSubmitAnswer}>
                        Submit Answer
                    </button>
                )}

                {currentQuestion === questions.length - 1 && (
                    <button className="submit-button" onClick={handleFinalSubmit}>
                        Final Submit
                    </button>
                )}
            </div>
        </div>
       </div>
    );
};

export default QuizPage;
