import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './QuizPage.css';

const QuizPage = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [userAnswers, setUserAnswers] = useState({});
    const [markedForReview, setMarkedForReview] = useState([]);
    const [isFinalSubmit, setIsFinalSubmit] = useState(false); // To track if it's time for final submission
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('https://krish-2512.github.io/api/questions-2.json');
                const shuffled = response.data.sort(() => 0.5 - Math.random()); // Shuffle questions randomly
                setQuestions(shuffled.slice(0, 10)); // Take only 10 questions
            } catch (err) {
                console.error("Error loading questions:", err);
            }
        };

        fetchQuestions();
    }, []);

    const handleAnswerChange = (answer) => {
        setUserAnswers((prev) => ({ ...prev, [currentQuestion]: answer }));
    };

    const handleLockAnswer = () => {
        // Mark the current answer as saved
        if (currentQuestion < questions.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        }
    };

    const handleMarkForReview = () => {
        setMarkedForReview((prev) => [...prev, currentQuestion]);
        if (currentQuestion < questions.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        }
    };

    const handleFinalSubmit = () => {
        // Submit logic for final answers
        console.log("User's answers:", userAnswers);
        console.log("Marked for review:", markedForReview);
        // Redirect to result page
        navigate('/result');
    };

    if (questions.length === 0) return <div>Loading...</div>;

    return (
        <div className="quiz-container">
            <h3>Question {currentQuestion + 1} of 10</h3>
            <div className="question">
                <h4>{questions[currentQuestion]?.question}</h4>
                <ul>
                    {['A', 'B', 'C', 'D'].map((option) => (
                        <li key={option}>
                            <label>
                                <input
                                    type="radio"
                                    name={`question-${currentQuestion}`}
                                    value={option}
                                    onChange={() => handleAnswerChange(option)}
                                    checked={userAnswers[currentQuestion] === option}
                                />
                                {questions[currentQuestion][option]}
                            </label>
                        </li>
                    ))}
                </ul>
            </div>

            <div className="navigation-buttons">
                <button
                    disabled={currentQuestion === 0}
                    onClick={() => setCurrentQuestion((prev) => prev - 1)}
                >
                    Previous Question
                </button>

                {currentQuestion < questions.length - 1 && (
                    <>
                        <button onClick={handleLockAnswer}>Save Answer</button>
                        <button onClick={handleMarkForReview}>Mark for Review</button>
                    </>
                )}

                {currentQuestion === questions.length - 1 && (
                    <button className="submit-button" onClick={handleFinalSubmit}>
                        Final Submit
                    </button>
                )}
            </div>

            <div className="extra-buttons">
                <button onClick={() => navigate('/summary')}>View Summary</button>
            </div>
        </div>
    );
};

export default QuizPage;
