import React, { useState, useEffect } from 'react';
import axios from 'axios';

const QuizPage = () => {
    const [questions, setQuestions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [userAnswers, setUserAnswers] = useState({}); // To track user's answers

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('https://krish-2512.github.io/api/questions-2.json');
                setQuestions(response.data); // Set questions from the response
            } catch (err) {
                setError(err);
            } finally {
                setLoading(false);
            }
        };

        fetchQuestions();
    }, []);

    const handleAnswerChange = (questionIndex, answer) => {
        setUserAnswers((prev) => ({ ...prev, [questionIndex]: answer }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle the quiz submission logic here
        console.log("User's answers:", userAnswers);
        // Calculate score and proceed to result page
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error loading questions: {error.message}</div>;

    return (
        <div>
        <form onSubmit={handleSubmit}>
            <div>
                {questions.length === 0 ? (
                    <div>No questions available.</div>
                ) : (
                    questions.map((question, index) => (
                        <div key={index}>
                            <h3>{question.question}</h3>
                            <ul>
                                {['A', 'B', 'C', 'D'].map((option) => (
                                    <li key={option}>
                                        <label>
                                            <input
                                                type="radio"
                                                name={`question-${index}`}
                                                value={option}
                                                onChange={() => handleAnswerChange(index, option)}
                                                checked={userAnswers[index] === option} // Manage checked state
                                            />
                                            {question[option]}
                                        </label>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))
                )}
            </div>
            <button type="submit">Submit Quiz</button>
        </form>
        <button onClick={() => navigate('/result')}>View Result</button>
        </div>
    );
};

export default QuizPage;
