import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './QuizPage.css';

const QuizPage = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [userAnswers, setUserAnswers] = useState({}); // To track user's answers
    const [timeRemaining, setTimeRemaining] = useState(300); 
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('https://krish-2512.github.io/api/questions-2.json');
                const shuffled = response.data.sort(() => 0.5 - Math.random()); // Shuffle questions randomly
                setQuestions(shuffled.slice(0, 10)); // Take only 10 questions
            } catch (err) {
                console.error("Error loading questions:", err);
            }
        };

        fetchQuestions();
    }, []);

    const handleAnswerChange = (answer) => {
        setUserAnswers((prev) => ({ ...prev, [currentQuestion]: answer }));
    };

    const handleSubmitAnswer = () => {
        // Simply saves the answer without changing questions
        console.log("Answer submitted for question:", currentQuestion, userAnswers[currentQuestion]);
        setCurrentQuestion((prev) => prev + 1);
    };

    const handleNextQuestion = () => {
        // Go to next question
        setCurrentQuestion((prev) => prev + 1);
    };

    const handleFinalSubmit = () => {
        // Handle the final submission logic
        console.log("Final submission of all answers:", userAnswers);
        navigate('/result'); // Redirect to result page
    };

    if (questions.length === 0) return <div>Loading...</div>;

    return (
        <div className="quiz-page">
        <div className="quiz-container">
            <h3>Question {currentQuestion + 1} of 10</h3>
            <div className="question">
                <h4>{questions[currentQuestion]?.question}</h4>
                <ul>
                    {['A', 'B', 'C', 'D'].map((option) => (
                        <li key={option}>
                            <label>
                                <input
                                    type="radio"
                                    name={`question-${currentQuestion}`}
                                    value={option}
                                    onChange={() => handleAnswerChange(option)}
                                    checked={userAnswers[currentQuestion] === option}
                                />
                                {questions[currentQuestion][option]}
                            </label>
                        </li>
                    ))}
                </ul>
            </div>

            <div className="navigation-buttons">
                {currentQuestion > 0 && (
                    <button onClick={() => setCurrentQuestion((prev) => prev - 1)}>
                        Previous Question
                    </button>
                )}

                {currentQuestion < questions.length - 1 && (
                    <button onClick={handleNextQuestion}>
                        Next Question
                    </button>
                )}
                {currentQuestion < questions.length - 1 && (
                    <button onClick={handleSubmitAnswer}>
                        Submit Answer
                    </button>
                )}

                {currentQuestion === questions.length - 1 && (
                    <button className="submit-button" onClick={handleFinalSubmit}>
                        Final Submit
                    </button>
                )}
            </div>
        </div>
       </div>
    );
};

export default QuizPage;
