import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useMutation } from 'react-query';
import axios from 'axios';
import './LoginPage.css'; // Include your custom styles here

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // React Query mutation for login
    const loginMutation = useMutation(
        async (userData) => {
            const response = await axios.post('/api/auth/login', userData);
            return response.data;
        },
        {
            onSuccess: (data) => {
                // Save the JWT token in localStorage (or cookies if preferred)
                localStorage.setItem('token', data.token);
                localStorage.setItem('user', JSON.stringify(data.user));
                navigate('/start-quiz'); // Redirect to Start Quiz Page
            },
            onError: () => {
                setError('Invalid email or password');
            }
        }
    );

    const handleSubmit = (e) => {
        e.preventDefault();
        setError('');
        loginMutation.mutate({ email, password });
    };

    return (
        <div className="login-page">
            <form onSubmit={handleSubmit} className="login-form">
                <h2>Login</h2>
                {error && <div className="error-message">{error}</div>}
                <div className="input-group">
                    <label>Email</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="input-group">
                    <label>Password</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="login-button">Login</button>
                <div className="signup-link">
                    <p>New user? <Link to="/register">Sign up here</Link></p>
                </div>
            </form>
        </div>
    );
};

export default LoginPage;
