import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useMutation } from 'react-query';
import './LoginPage.css';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showRegister, setShowRegister] = useState(false); // To toggle between login and register forms
    const navigate = useNavigate();

    const loginMutation = useMutation(
        async (userData) => {
            const response = await axios.post('/api/auth/login', userData);
            return response.data;
        },
        {
            onSuccess: (data) => {
                // Save the JWT token in localStorage (or cookies if preferred)
                localStorage.setItem('token', data.token);
                localStorage.setItem('user', JSON.stringify(data.user));
                navigate('/start-quiz'); // Redirect to Start Quiz Page
            },
            onError: () => {
                setError('Invalid email or password');
            }
        }
    );

    // Function to handle login submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/api/users/login', { email, password });
            localStorage.setItem('token', res.data.token);
            navigate('/start-quiz');
        } catch (error) {
            console.error('Login failed:', error);
        }
    };


    const handleRegisterSubmit = async (e) => {
        e.preventDefault();
        // Implement register logic here using form inputs
        console.log('Register form submitted');
    };

    return (
        <div className="container">
            <div className="form-container">
                {/* Conditionally render based on `showRegister` state */}
                {!showRegister ? (
                    // Login Form
                    <form id="login-form" className="form" onSubmit={handleSubmit}>
                        <h2>Login</h2>
                        <input 
                            type="email" 
                            placeholder="Email" 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                            required 
                        />
                        <input 
                            type="password" 
                            placeholder="Password" 
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                        <button type="submit">Login</button>
                        <p>
                            Don't have an account?{' '}
                            <a href="#" onClick={() => setShowRegister(true)}>Register</a>
                        </p>
                    </form>
                ) : (
                    // Register Form
                    <form id="register-form" className="form" onSubmit={handleRegisterSubmit}>
                        <h2>Register</h2>
                        <input type="text" placeholder="Name" required />
                        <input type="text" placeholder="Mobile Number" required />
                        <input type="email" placeholder="Email" required />
                        <input type="password" placeholder="Password" required />
                        <input type="password" placeholder="Confirm Password" required />
                        <button type="submit">Register</button>
                        <p>
                            Already have an account?{' '}
                            <a href="#" onClick={() => setShowRegister(false)}>Login</a>
                        </p>
                    </form>
                )}
            </div>
        </div>
    );
};

export default LoginPage;
