// QuizPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './QuizPage.css';

const QuizPage = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [userAnswers, setUserAnswers] = useState({});
    const [markedForReview, setMarkedForReview] = useState({});
    const [skippedQuestions, setSkippedQuestions] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('https://krish-2512.github.io/api/questions-2.json');
                // Shuffle and pick 10 random questions
                const shuffledQuestions = response.data.sort(() => 0.5 - Math.random());
                setQuestions(shuffledQuestions.slice(0, 10));
            } catch (err) {
                console.error("Error loading questions:", err);
            }
        };

        fetchQuestions();
    }, []);

    const handleAnswerChange = (answer) => {
        setUserAnswers((prev) => ({ ...prev, [currentQuestionIndex]: answer }));
    };

    const markForReview = () => {
        setMarkedForReview((prev) => ({ ...prev, [currentQuestionIndex]: true }));
        setCurrentQuestionIndex(currentQuestionIndex + 1);
    };

    const nextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
        }
    };

    const prevQuestion = () => {
        if (currentQuestionIndex > 0) {
            setCurrentQuestionIndex(currentQuestionIndex - 1);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle quiz submission logic and show summary
        console.log("User's answers:", userAnswers);
        navigate('/result');
    };

    return (
        <div className="quiz-container">
            {questions.length > 0 ? (
                <>
                    <h3>Question {currentQuestionIndex + 1} of {questions.length}</h3>
                    <div className="question">
                        <h4>{questions[currentQuestionIndex].question}</h4>
                        <ul>
                            {['A', 'B', 'C', 'D'].map((option) => (
                                <li key={option}>
                                    <label>
                                        <input
                                            type="radio"
                                            name={`question-${currentQuestionIndex}`}
                                            value={option}
                                            onChange={() => handleAnswerChange(option)}
                                            checked={userAnswers[currentQuestionIndex] === option}
                                        />
                                        {questions[currentQuestionIndex][option]}
                                    </label>
                                </li>
                            ))}
                        </ul>
                    </div>
                    <div className="navigation-buttons">
                        <button onClick={prevQuestion} disabled={currentQuestionIndex === 0}>
                            Previous
                        </button>
                        <button onClick={nextQuestion} disabled={currentQuestionIndex === questions.length - 1}>
                            Next
                        </button>
                        <button onClick={markForReview}>Mark for Review</button>
                    </div>
                    
                    <button className="submit-button" onClick={handleSubmit}>
                        Submit Quiz
                    </button>
                </>
            ) : (
                <div>Loading questions...</div>
            )}
        </div>
    );
};

export default QuizPage;
